import React from 'react';
import Form from './Form';

class Post extends React.Component {
  state = {
    names: []
  }

  // addPost = (event) => {
  //   this.setState({names: 'ehy'})
  // }

  addPost = (event) => {
    // let name = event.target.value
    this.setState({
      names: 'hey'
    })
  }

  render() {
    return (
      <div>
        <Form names={this.state.names} addPost={this.addPost}/>
      </div>
    )
  }
}

export default Post;
