import React from 'react';
import Form from './Form';

class Post extends React.Component {
  state = {
    names: []
  }

  handleChange = (event) => {
    let name = event.target.value
    this.setState({
      name: this.state.name
    })
  }

  render() {
    return (
      <div>
        <Form names={this.state.names} addPost={this.addPost}/>
      </div>
    )
  }
}

export default Post;
