import React from 'react';
import Form from './Form';

class Post extends React.Component {
  state = {
    names: []
  }

  addPost = (event, name) => {
    event.preventDefault();
    console.log(name)
    // let name = event.target.value
    this.setState({
      names: name
    })
  }

  render() {
    return (
      <div>
        <Form names={this.state.names} addPost={this.addPost} />
      </div>
    )
  }
}

export default Post;
