import React from 'react';

class Form extends React.Component {

  state = {
    name: ''
  }

  handleChange = (event) => {
    let name = event.target.value
    this.setState({
      name: name
    })
    console.log(this.state.name)

  }

  submitName(event) {
    event.preventDefault();
    this.props.addPost('hey');
  }

  render() {
    return (
      <div className="form">
        <form>
          <input onChange={(event) => this.handleChange(event)}/>
          <input type="submit" value="Submit" onSubmit = {(event) => this.submitName(event)} />
        </form>
      </div>
    )
  }
}

export default Form;
