import React from 'react';
import Form from './Form';

class AllPosts extends React.Component {
  state = {
    names: []
  }

  addPost = (name) => {
    console.log(this.state.names)
    this.setState({
      names: name
    })
  }

  render() {
    return (
      <div>
        <Form names={this.state.names} addPost={this.addPost} />
      </div>
    )
  }
}

export default AllPosts;
