import React from 'react';

class Form extends React.Component {

  state = {
    name: ''
  }

  handleChange = (event) => {
    let name = event.target.value
    this.setState({
      name: this.state.name
    })
  }

  submitName(event) {
    this.props.addPost('hey');
    event.preventDefault();
  }

  render() {
    return (
      <div className="form">
        <form>
          <input onChange={(event) => this.handleChange(event)}/>
          <button value="Submit" onSubmit = {(event) => this.submitName(event)} />
        </form>
      </div>
    )
  }
}

export default Form;
